import request from "@/utils/request";

export function getSysOperationRecordList(query){
    return request({
        url: "/operation/get_operations",
        method: "get",
        params: query,
    });
}

export function deleteSysOperationRecord(id){
    return request({
        url: `/operation/${id}/delete_operation`,
        method: "delete",
    });
}

export function deleteSysOperationRecordByIds(data){
    return request({
        url: "/operation/delete_operations",
        method: "post",
        data,
    });
}
